pragma solidity 0.4.24;

import "./../installed_contracts/ownership/Ownable.sol";

contract PlayerIterator {
    struct Player {
        address addr;
        uint8 bid;
    }

    Player[] players;

    function getPeople(uint[] indexes)
        public
        returns (address[], uint8[])
    {
        address[] memory addrs = new address[](indexes.length);
        uint8[]   memory bids = new uint8[](indexes.length);

        for (uint i = 0; i < indexes.length; i++) {
            Player storage person = players[indexes[i]];
            addrs[i] = person.addr;
            bids[i] = person.bid;
        }

        return (addrs, bids);
    }
}

contract Game is Ownable, PlayerIterator {
    enum Stages {
        NotStarted,
        Started,
        EndRound
    }
    // This is the current stage.
    Stages public stage = Stages.NotStarted;

    uint public endTime;
    mapping(address => uint8) public players;
    uint public joinPrice;

    // event Transfer(address indexed _from, address indexed _to, uint256 _value);

    // function update(uint newBalance) public {
    //     bank[msg.sender] = newBalance;
    // }

    // -----------------------------------------
    // public methods

    // Accumulate some deposit
    function() payable public {
        // @todo - check if value correct with modifier(if game not starded - value is ok, if started check if ==)
        // require(msg.value > 1 ether);

        if(atStage(Stages.NotStarted)) {
            startGame(msg.value);
        } 

        // @todo - get bid (0- rock, 1- siccors, -1- paper)
        // addParticipant(msg.sender, bid);

    }

    function getContractBalance() public returns (uint) {
        return this.balance;
    }

    // -----------------------------------------
    // internal methods
    // modifier atStage(Stages _stage) {
    //     require(
    //         stage == _stage,
    //         "Function cannot be called at this time."
    //     );
    //     _;
    // }

    function atStage(Stages _stage) private returns (bool) {
        return (stage == _stage) ? true : false;
    }

    function nextStage() internal {
        stage = Stages(uint(stage) + 1);
    }

    function startGame(uint bid) internal {
        joinPrice = bid;
        endTime = block.timestamp + 10 minutes;
        nextStage();
    }

    function addParticipant(address _address, uint8 bid) internal {
        players[_address] = bid;
    }

    // -----------------------------------------
    // test
    function goToNext() public returns (Stages) {
        nextStage();
        return stage;    
    }

}
